function add(a,b){
    return a + b;
}
function sub(a,b){
    return a - b;

}
function mul(a,b){
    return a * b;
}
function div(a,b){
    return a / b;


    }

    
function fact(a){
    if(a==0 || a==1) return 1;
    else{
        return a*fact(a-1);

    }
}
module.exports = {add, sub, mul, div,fact};